import React from "react";
import { exchange } from "../components/api";
import { useState, useEffect } from "react";
import { Loading } from "../components/Loading";
import { Exchange } from "../components/Exchanges";

export const Exchanges = () => {
  let [exchanges, setExchanges] = useState([]);
  let [loading, setLoading] = useState(false);
  async function fetchData() {
    let exchangeS = await exchange();
    exchangeS = exchangeS.slice(0, 500);
    setExchanges(exchangeS);
  }
  useEffect(() => {
    setLoading(true);
    fetchData();
    setLoading(false);
  }, []);

  return (
    <div>
      {console.log(exchanges)}
      {loading ? (
        <Loading />
      ) : (
        exchanges.map((exchange) => <Price exchange={exchange} />)
      )}
    </div>
  );
};
